  `id` int(11) NOT NULL AUTO_INCREMENT,
  `novel_name` varchar(30) COLLATE utf8_croatian_ci DEFAULT NULL,
  `novel_image` varchar(100) COLLATE utf8_croatian_ci DEFAULT NULL,
  `novel_byte` int(11) NOT NULL,
  `novel_read` int(11) NOT NULL,
  `novel_comment` int(11) NOT NULL,
  `novel_text` longtext COLLATE utf8_croatian_ci,
  `novel_fave` int(11) NOT NULL,
  `novel_next` int(11) NOT NULL,
  `novel_time` datetime(6) NOT NULL,
  `novel_tag_id` int(11) DEFAULT NULL,
  `novel_user_id` int(11) DEFAULT NULL,
  `is_long` tinyint(1) NOT NULL,
  `novel_Recommend` tinyint(1) NOT NULL,
  `novel_alias` varchar(30) COLLATE utf8_croatian_ci,
  PRIMARY KEY (`id`),
  KEY `NovelModel_novel_tag_id_f55a530c_fk_TagModel_id` (`novel_tag_id`),
  KEY `NovelModel_novel_user_id_20f12ff1_fk_users_userprofile_id` (`novel_user_id`),
